import axios from "axios";
import {
  GET_ERRORS,
  GET_BACKLOG,
  GET_PROJECT_TASK,
  DELETE_PROJECT_TASK
} from "./types";

export const addProjectTask = (
  backlog_id,
  project_task,
  history
) => async dispatch => {
  try {
    await axios.post(`/api/backlog/${backlog_id}`, project_task); // base URL is proxy defined in package.json
    history.push(`/projectBoard/${backlog_id}`);

    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getBacklog = (backlog_id, history) => async dispatch => {
  try {
    const res = await axios.get(`/api/backlog/${backlog_id}`, backlog_id); //base URL is proxy defined in package.json
    dispatch({
      type: GET_BACKLOG,
      payload: res.data
    });
  } catch (errors) {
    history.push(`/projectBoard/${backlog_id}`);
  }
};
