import {
  GET_BACKLOG,
  GET_PROJECT_TASK,
  DELETE_PROJECT_TASK
} from "../actions/types";

const intialState = {
  project_tasks: [],
  project_task: {}
};

export default function(state = intialState, action) {
  switch (action.type) {
    case GET_BACKLOG:
      return {
        ...state,
        project_tasks: action.payload
      };
    case GET_PROJECT_TASK:
      return {
        ...state,
        project_task: action.payload
      };
    case DELETE_PROJECT_TASK:
      return {
        ...state,

        project_task: state.project_tasks.filter(
          project_tasks => project_tasks.projectIdentifier !== action.payload
        )
      };

    default:
      return state;
  }
}
