import { GET_PROJECTS, GET_PROJECT, DELETE_PROJECT } from "../actions/types";

const intialState = {
  projects: [],
  project: {}
};

export default function(state = intialState, action) {
  switch (action.type) {
    case GET_PROJECTS:
      return {
        ...state,
        projects: action.payload
      };

    case GET_PROJECT:
      return {
        ...state,
        project: action.payload
      };

    case DELETE_PROJECT:
      return {
        ...state,
        //projects: action.payload //this line would make a GET call to backend but following filter function removes the deleted project from the list using redux
        project_tasks: state.projects.filter(
          project_tasks => project_tasks.projectIdentifier !== action.payload
        )
      };

    default:
      return state;
  }
}
